import { useState } from "react";
import Button from "./Button";
import Input from "./Input";

export default function Page() {
  const [formData, setFormData] = useState({
    username: "abdo",
    email: "abdo@google.com",
    age: "34",
    password: "*****1244",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = () => {
    console.log(formData);
  };

  return (
    <div className="mt-20 flex-col flex items-center justify-center w-screen">
      <div className="w-[300px]">
        <h1 className="text-3xl mb-4">Form</h1>
        <div role="alert" className="alert text alert-success">
          <div className="icon block">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              className="stroke-current  shrink-0 h-6 w-6"
              fill="none"
              viewBox="0 0 24 24">
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z"
              />
            </svg>
          </div>
          <ul>
            {Object.entries(formData).map(([key, value], idx) => (
              <li key={idx}>
                {key}: {value}
              </li>
            ))}
          </ul>
        </div>
        <div className="form">
          <Input
            type="text"
            holder="Enter Your Username"
            name="username"
            value={formData.username}
            onChange={handleChange}
          />
          <Input
            type="email"
            holder="Enter Your Email"
            name="email"
            value={formData.email}
            onChange={handleChange}
          />
          <Input
            type="number"
            holder="Enter Your Age"
            name="age"
            value={formData.age}
            onChange={handleChange}
          />
          <Input
            type="password"
            holder="Enter Your Password"
            name="password"
            value={formData.password}
            onChange={handleChange}
          />
          <Button fn={handleSubmit} value="Submit" />
        </div>
      </div>
    </div>
  );
}
