import { useRef, useState } from "react";
import Button from "./Button";
import Input from "./Input";

export default function Page() {
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    age: "",
    password: "",
  });

  const usernameRef = useRef("");
  const emailRef = useRef("");
  const ageRef = useRef("");
  const passRef = useRef("");
  const myRef = useRef();

  const handleChange = (e) => {};

  const handleSubmit = (e) => {
    e.preventDefault(); // prevent form from refreshing the page
    const data = myRef.current.value; // get the value of the input field

    console.log(data);
  };

  return (
    <div className="mt-20 flex-col flex items-center justify-center w-screen">
      <div className="w-[300px]">
        <h1 className="text-3xl mb-4">Form</h1>
        <div className="form">
          <Input type="text" holder="Enter Your Username" ref={usernameRef} />
          <Input type="email" holder="Enter Your Email" ref={emailRef} />
          <Input type="number" holder="Enter Your Age" ref={ageRef} />
          <Input type="password" holder="Enter Your Password" ref={passRef} />
          <Input ref={myRef} type="password" holder="Enter Your Password" />
          <Button fn={handleSubmit} value="Submit" />
        </div>
      </div>
    </div>
  );
}
