import React, { useRef, useState } from "react";
import Button from "./Button";
import Input from "./Input";

export default function Page() {
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    age: "",
    password: "",
  });

  const [errors, setErrors] = useState({
    username: "",
    email: "",
    age: "",
    password: "",
  });

  const usernameRef = useRef();
  const emailRef = useRef();
  const ageRef = useRef();
  const passRef = useRef();

  const validateEmail = (email) => {
    // Basic email validation
    return /\S+@\S+\.\S+/.test(email);
  };

  const handleSubmit = () => {
    const newErrors = {
      username: !formData.username ? "Username is required" : "",
      email: !formData.email ? "Email is required" : validateEmail(formData.email) ? "" : "Invalid email",
      age: !formData.age ? "Age is required" : isNaN(formData.age) ? "Age must be a number" : "",
      password: !formData.password ? "Password is required" : "",
    };

    setErrors(newErrors);

    // If there are no errors, proceed with form submission
    if (Object.values(newErrors).every((error) => !error)) {
      // Perform form submission
      console.log("Form submitted successfully");
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  return (
    <div className="mt-20 flex-col flex items-center justify-center w-screen">
      <div className="w-[300px]">
        <h1 className="text-3xl mb-4">Form</h1>
        <div className="form">
          <Input type="text" holder="Enter Your Username" ref={usernameRef} onChange={handleChange} name="username" />
          {errors.username && <p className="text-red-500">{errors.username}</p>}
          <Input type="email" holder="Enter Your Email" ref={emailRef} onChange={handleChange} name="email" />
          {errors.email && <p className="text-red-500">{errors.email}</p>}
          <Input type="number" holder="Enter Your Age" ref={ageRef} onChange={handleChange} name="age" />
          {errors.age && <p className="text-red-500">{errors.age}</p>}
          <Input type="password" holder="Enter Your Password" ref={passRef} onChange={handleChange} name="password" />
          {errors.password && <p className="text-red-500">{errors.password}</p>}
          <Button fn={handleSubmit} value="Submit" />
        </div>
      </div>
    </div>
  );
}
