import { useRef, useState } from "react";
import Button from "./Button";
import Input from "./Input";

export default function Page() {
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    age: "",
    password: "",
  });

  const usernameRef = useRef("");
  const emailRef = useRef("");
  const ageRef = useRef("");
  const passRef = useRef("********");

  const handleSubmit = (e) => {
    const username = usernameRef?.current.value;
    const email = emailRef.current?.value;
    const age = ageRef.current?.value || "***********";
    const password = passRef.current?.value;
    setFormData({
      username,
      email,
      age,
      password,
    });

    console.log(username);
    console.log(age);
    console.log(email);
    console.log(password);
    console.log(formData);
  };

  return (
    <div className="mt-20 flex-col flex items-center justify-center w-screen">
      <div className="w-[300px]">
        <h1 className="text-3xl mb-4">Form</h1>
        <div className="errors">
          <div role="alert" className="alert alert-error p-2">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              className="stroke-current shrink-0 h-6 w-6"
              fill="none"
              viewBox="0 0 24 24">
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z"
              />
            </svg>
            <span>Warning: Invalid email address!</span>
          </div>
        </div>
        <div className="form">
          <Input refs={usernameRef} type="text" holder="Enter Your Username" />
          <Input refs={emailRef} type="email" holder="Enter Your Email" />
          <Input refs={ageRef} type="number" holder="Enter Your Age" />
          <Input ref={passRef} type="password" holder="Enter Your Password" />
          <Button fn={handleSubmit} value="Submit" />
        </div>
      </div>
    </div>
  );
}
