import React, { useRef, useState } from "react";
import Button from "./Button";
import Input from "./Input";

export default function Page() {
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    age: "",
    password: "",
  });

  const usernameRef = useRef();
  const emailRef = useRef();
  const ageRef = useRef();
  const passRef = useRef();

  const handleSubmit = () => {
    console.log(usernameRef.current?.value);
    // Here, you can access the values from the refs and update your state or perform any other actions
  };

  const handleChange = (e) => {
    // Here, you should update the state based on the form input changes
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  return (
    <div className="mt-20 flex-col flex items-center justify-center w-screen">
      <div className="w-[300px]">
        <h1 className="text-3xl mb-4">Form</h1>
        <div className="form">
          <Input
            type="text"
            holder="Enter Your Username"
            ref={usernameRef}
            onChange={handleChange}
            name="username"
          />
          <Input
            type="email"
            holder="Enter Your Email"
            ref={emailRef}
            onChange={handleChange}
            name="email"
          />
          <Input
            type="number"
            holder="Enter Your Age"
            ref={ageRef}
            onChange={handleChange}
            name="age"
          />
          <Input
            type="password"
            holder="Enter Your Password"
            ref={passRef}
            onChange={handleChange}
            name="password"
          />
          <Button fn={handleSubmit} value="Submit" />
        </div>
      </div>
    </div>
  );
}
